import { Link } from "react-router-dom";
import "./sidebar.css";
import { useEffect, useState } from "react";
import axios from "axios";
export default function Sidebar() {
  const [cats,setCats] = useState([]);

  useEffect(() => {
    const getCats = async () => {
      const res = await axios.get("/categories");
      setCats(res.data);
    };
    getCats();
  },[])
  return (
    <div className="sidebar">
      <div className="sidebarItem">
        <span className="sidebarTitle">ABOUT US</span>
        <img
          src="https://scontent.fgau4-1.fna.fbcdn.net/v/t1.6435-9/135449261_110108704334612_8228861415113150782_n.jpg?_nc_cat=111&ccb=1-5&_nc_sid=730e14&_nc_ohc=wbhPBOI53woAX_DBwTA&_nc_ht=scontent.fgau4-1.fna&oh=00_AT_kR54arl9eb4GeMpLHhgh9L-ot9DPQ_q75PjoXd6bBgw&oe=6277AF23"
          alt=""
        />
        <p>
         My name is kanduri. And i cry a lot. 
        </p>
      </div>
      <div className="sidebarItem">
        <span className="sidebarTitle">CATEGORIES</span>
        <ul className="sidebarList">
          {cats.map((c) => (
            <Link>
            <li className="sidebarListItem">{c.name}</li>
            </Link>
          ))}
        </ul>
      </div>
      <div className="sidebarItem">
        <span className="sidebarTitle">FOLLOW US</span>
        <div className="sidebarSocial">
          <i className="sidebarIcon fab fa-facebook-square"></i>
          <i className="sidebarIcon fab fa-instagram-square"></i>
          <i className="sidebarIcon fab fa-pinterest-square"></i>
          <i className="sidebarIcon fab fa-twitter-square"></i>
        </div>
      </div>
    </div>
  );
}
